			$.post("{{{ url('/user/unblock') }}}", { user_id:user_id, _token: App.csrf_token }, function(data){


                    if(data.status=='error')
					{
						toastr.info("{{{trans_choice('app.block_msg',1)}}}");
					}
					else
					{
						toastr.info("{{{trans_choice('app.block_msg',0)}}}");
							        	
						
						$("#block"+user_id).fadeOut();
							        								        	
					}
                


            });
        
		});
		
